mapper.select:
SELECT DISTINCT ta.*
FROM product ta LEFT JOIN product_employee tb ON ta.id = tb.product_id
WHERE

  (tb.account_id = #{accountId}
  OR ta.contact_account_id = #{accountId}
  OR ta.type=1)


  (tb.account_id = #{accountId}
  OR ta.contact_account_id = #{accountId})


  AND ta.status= #{productStatus}


  AND (tb.employee_name LIKE concat('%',#{searchText},'%') OR ta.name LIKE concat('%',#{searchText},'%') OR ta.contact_employee_name LIKE concat('%',#{searchText},'%'))

ORDER BY ta.modified_time DESC
