mapper.select:
select

from program_approval_snapshot


    id = #{id}


    and program_id = #{programId}


      and product_id = #{productId}


      and program_status = #{programStatus}


    and approval_status= #{approvalStatus}


    and bpm_code= #{bpmCode}


ORDER  BY  create_time DESC

select

from program_approval_snapshot


    id = #{id}


    and program_id = #{programId}


    and approval_status= #{approvalStatus}


