            #{item}



    and bpm_code= #{bpmCode}


ORDER  BY  create_time DESC

select * from ( SELECT p.id program_id,p.name,p.product_id,p.approval_status, CASE WHEN pas.program_status = '200' THEN pas.program_status WHEN pas.program_status = '999' THEN pas.program_status ELSE p.program_status END program_status, CASE WHEN pas.program_status = '200' THEN pas.create_time WHEN pas.program_status = '999' THEN pas.create_time WHEN p.program_status = '100' THEN p.commit_date WHEN p.program_status = '110' THEN p.commit_date WHEN p.program_status = '120' THEN p.demo_approval_date WHEN p.program_status = '130' THEN p.bidding_date WHEN p.program_status = '140' THEN p.winning_bid_date WHEN p.program_status = '150' THEN p.prod_approval_date WHEN p.program_status = '160' THEN p.dev_approval_date WHEN p.program_status = '170' THEN p.test_approval_date WHEN p.program_status = '180' THEN p.online_plan_date WHEN p.program_status = '190' THEN p.gray_release_date WHEN p.program_status = '193' THEN p.all_extension_date WHEN p.program_status = '195' THEN p.replay_date WHEN p.program_status = '900' THEN pas.create_time ELSE pas.create_time END create_time, p.warning_days, (SELECT pw.warning from program_warning pw where pw.program_id=pas.program_id ORDER BY warning desc LIMIT 0,1) warning from program p INNER JOIN product pt on pt.id=p.product_id INNER JOIN (SELECT * from program_approval_snapshot GROUP BY program_id ORDER BY create_time desc ) pas on pas.program_id=p.id where ( p.warning_days < 0 or (pas.program_status IN ('999', '200') AND pas.approval_status = 110 ))
and pas.product_id in
#{item}
and pt.analyzing_conditions LIKE CONCAT('%', #{analyzingConditions, jdbcType=VARCHAR}, '%')
order by pas.create_time DESC ) a order by a.create_time DESC LIMIT #{startRow},#{endRow}

select COUNT(program_id) from ( SELECT p.id program_id,pas.name,pas.product_id,p.program_status,pas.create_time from program p LEFT JOIN (SELECT * from program_approval_snapshot GROUP BY program_id ORDER BY create_time desc ) pas on pas.program_id=p.id where pas.program_status in ('999','200') and pas.approval_status = 110
and pas.product_id in
#{item}
and pas.analyzing_conditions = #{analyzingConditions}
or p.warning_days < 0 order by pas.create_time DESC ) a

SELECT * from program_approval_snapshot
