SELECT DISTINCT p.id, p.product_id, p.product_name, p.product_code, p.name, p.descp,p.architecture_date,p.overall_cost, p.dev_workload,p.analyzing_conditions, p.like_product, p.like_program, p.type, p.program_status, p.modified_account_id, p.modified_name, p.create_time, p.modified_time,p.approval_status,p.commit_date,p.demo_approval_date,p.bidding_date,p.winning_bid_date, p.prod_approval_date,p.dev_approval_date,p.test_approval_date,p.online_plan_date,p.gray_release_date,p.replay_date, p.all_extension_date,p.actual_commit_date,p.actual_demo_approval_date,p.actual_bidding_date,p.actual_winning_bid_date, p.actual_prod_approval_date,p.actual_dev_approval_date,p.actual_test_approval_date,p.actual_online_plan_date, p.actual_gray_release_date,p.actual_all_extension_date,p.actual_replay_date FROM program p JOIN program_employee pe ON p.id = pe.program_id left join product pt on pt.id = p.product_id WHERE 1=1
and p.program_status != 100
and (pe.account_id = #{accountId, jdbcType=VARCHAR} OR p.type = 1)
and pe.account_id = #{accountId, jdbcType=VARCHAR}
and pe.account_id = #{accountId, jdbcType=VARCHAR}
and p.program_status in
#{item}
and p.approval_status = #{approvalStatus}
and (p.product_id =#{productId})
and (p.name like concat(concat('%',#{searchText}),'%') OR pe.employee_name like concat(concat('%',#{searchText}),'%'))
and p.analyzing_conditions = #{analyzingConditions}
and pt.label like concat(concat('%',#{label}),'%')
ORDER BY p.create_time DESC

SELECT DISTINCT p.id,p.`name`, (select IFNULL(sum(CASE WHEN d.relation_id =p.id THEN 1 ELSE 0 END),0) from demand d where d.relation_type=2 and `status`=2)+ (select IFNULL(sum(CASE WHEN b.relation_id =p.id THEN 1 ELSE 0 END),0) from bug_info b where b.relation_type=2 and `status`=2) as pending, (select IFNULL(sum(CASE WHEN d.relation_id =p.id THEN 1 ELSE 0 END),0) from demand d where d.relation_type=2 and `status`=3)+ (select IFNULL(sum(CASE WHEN b.relation_id =p.id THEN 1 ELSE 0 END),0) from bug_info b where b.relation_type=2 and `status`=3) as working, (select IFNULL(sum(CASE WHEN d.relation_id =p.id THEN 1 ELSE 0 END),0) from demand d where d.relation_type=2 and `status`=4)+ (select IFNULL(sum(CASE WHEN b.relation_id =p.id THEN 1 ELSE 0 END),0) from bug_info b where b.relation_type=2 and `status`=4) as complete FROM program p JOIN program_employee pe ON p.id = pe.program_id WHERE 1=1
and (pe.account_id = #{accountId, jdbcType=VARCHAR} OR p.type = 1)
and pe.account_id = #{accountId, jdbcType=VARCHAR}
and pe.account_id = #{accountId, jdbcType=VARCHAR}
and p.program_status = #{programStatus}
and p.product_id = #{productId}
ORDER BY p.create_time DESC

